USE BUDT758_DB_0504_10CREATE TABLE [TerpEats.Customer] (	customerId CHAR (10) NOT NULL UNIQUE,	customerLastName VARCHAR (20),	customerFirstName VARCHAR (20),	customerEmail VARCHAR (30), -- Can make this unique, not sure if that messed with 3rd NF	customerZipCode CHAR (9),	customerDateOfBirth DATE,	CONSTRAINT pk_Customer_customerId PRIMARY KEY (customerId) );

CREATE TABLE [TerpEats.Menu] (	menuId CHAR(10) NOT NULL UNIQUE,	menuURL VARCHAR(100),	CONSTRAINT pk_Food_menuId PRIMARY KEY (menuId) );CREATE TABLE [TerpEats.Location] (	locationID CHAR (10) NOT NULL UNIQUE,	locationName VARCHAR (30),	locationLongitude FLOAT,	locationLatitude FLOAT,	CONSTRAINT pk_Location_locationID PRIMARY KEY (locationID) );CREATE TABLE [TerpEats.Restaurant] (	restaurantId CHAR (10) NOT NULL UNIQUE,	restaurantName VARCHAR (50),	restaurantStreet VARCHAR (50), 	restaurantCity VARCHAR (50),  	restaurantState CHAR (2), 	restaurantZipCode CHAR (9),	restaurantPhoneNo CHAR (10), 	locationId CHAR (10),	menuId CHAR(10),	CONSTRAINT pk_Restaurant_restaurantId PRIMARY KEY (restaurantId),	CONSTRAINT fk_Restaurant_locationId FOREIGN KEY (locationId)		REFERENCES [TerpEats.Location] (locationId)		ON DELETE NO ACTION ON UPDATE CASCADE,	CONSTRAINT fk_Restaurant_menuId FOREIGN KEY (menuId)		REFERENCES [TerpEats.Menu] (menuId)		ON DELETE NO ACTION ON UPDATE CASCADE );CREATE TABLE [TerpEats.Employee] (	employeeId CHAR (12) NOT NULL UNIQUE,	employeeLastName VARCHAR (20), 	employeeFirstName VARCHAR (20), 	employeePosition VARCHAR (20),	employeeSalary NUMERIC,	restaurantId CHAR (10),	CONSTRAINT pk_Employee_employeeId PRIMARY KEY (employeeId),	CONSTRAINT fk_Employee_restaurantId FOREIGN KEY (restaurantId)		REFERENCES [TerpEats.Restaurant] (restaurantId)		ON DELETE CASCADE ON UPDATE CASCADE );
CREATE TABLE [TerpEats.Dine] (	restaurantId CHAR (10) NOT NULL,	customerId CHAR (10) NOT NULL,	CONSTRAINT pk_Dine_restaurantId_customerId PRIMARY KEY (restaurantId, customerId),	CONSTRAINT fk_Dine_restaurantId FOREIGN KEY (restaurantId)		REFERENCES [TerpEats.Restaurant] (restaurantId)		ON DELETE CASCADE ON UPDATE CASCADE,	CONSTRAINT fk_Dine_customerId FOREIGN KEY (customerID)		REFERENCES [TerpEats.Customer] (customerId)		ON DELETE NO ACTION ON UPDATE CASCADE );
CREATE TABLE [TerpEats.Review] (	reviewId CHAR (10) NOT NULL UNIQUE,	reviewRating DECIMAL(2,1),	reviewText NVARCHAR (MAX), 	CONSTRAINT pk_Review_reviewId PRIMARY KEY (reviewId) );CREATE TABLE [TerpEats.Write] (	restaurantId CHAR (10) NOT NULL,	reviewId CHAR (10) NOT NULL UNIQUE,	customerId CHAR (10) NOT NULL,	CONSTRAINT pk_Write_reviewId PRIMARY KEY (reviewId),	CONSTRAINT fk_Write_restaurantId FOREIGN KEY (restaurantId)	REFERENCES [TerpEats.Restaurant] (restaurantId)		ON DELETE CASCADE ON UPDATE CASCADE,	CONSTRAINT fk_Write_reviewId FOREIGN KEY (reviewId)		REFERENCES [TerpEats.Review] (reviewId)		ON DELETE CASCADE ON UPDATE CASCADE,	CONSTRAINT fk_Write_customerId FOREIGN KEY (customerId)	REFERENCES [TerpEats.Customer] (customerId)		ON DELETE NO ACTION ON UPDATE CASCADE );